return(
<div className ="App">
<h1>Todo List</h1>
<form onSubmit={handlesubmit}>
    <input 
        type ="text" 
        align ="right" 
        onChange={(e) => setTodo(e.target.va)}
    />
    <button type ="submit">Add Todo</button>
</form>
</div>
);
};
export default App;
